import React from 'react';
import styles from './BuildControls.module.css';
import BuildControl from './BuildControl/BuildControl';


const controls = [{label:'Salad' , type:'salad'},{label:'Cheese' , type:'cheese'}, {label:'Meat' , type:'meat'},{label:'Bacon' , type:'bacon'}];

const BuildControls = (props) => (
    
        <div className={styles.BuildControls}>
            <p>Current Price: <strong>{props.price.toFixed(2)}</strong></p>
            {
            controls.map(ctrl => (
                <BuildControl 
                key={ctrl.label}
                 label={ctrl.label} 
                 //add burgerbuilder controls 
                 added={() => props.ingredientAdded(ctrl.type)}
                 //you could add types by type={ctrl.type instead we use above adding as argument ctrltype to ingredientAdded}
                 removed={()=>props.ingredientRemoved(ctrl.type)}
                 disabled={props.disabled[ctrl.type]}
                 />
            ))}

            <button className={styles.OrderButton}
            disabled={props.purchasable}> ORDER NOW </button>
        </div>
    
);


export default BuildControls;
