import React , {Component} from 'react';
import Aux from '../../hoc/Auxillary';
import styles from './Layout.module.css';
import Toolbar from '../Navigation/Toolbar/Toolbar';
import SideDrawer from '../Navigation/SideDrawer/SideDrawer';


// layout functioin turned to class to handle calls to backdrop at sidedrawer
//const layout = (props) => (
//    <Aux>
//        <Toolbar />
//        <SideDrawer/>
//        <main className={styles.Content}>
//            {props.children}
//        </main>
//   </Aux>
//  );

// create a stateful layout component with closedsidedrawer arrow function 
// which is passed down to the sideDrwaer and backdrop

class Layout extends Component {

    state = {

        showSideDrawer:true 
    }

    closedSideDrawHandler = ()=> {
        this.setState( {showSideDrawer:false} );
    }

    render (){
    return (
    // pass the open property to showsidedrawer and sidedrawer
                <Aux>
                    <Toolbar />
                    <SideDrawer closed = {this.closedSideDrawHandler} 
                    open = {this.state.showSideDrawer} />
                    <main className={styles.Content}>
                        {this.props.children}
                    </main>
                </Aux>

    );
    }
 }

export default Layout;
